-- ----------------------------
-- Table structure for blog_user
-- ----------------------------
DROP TABLE IF EXISTS `blog_user`;
CREATE TABLE `blog_user` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '自动编号',
  `username` char(20) NOT NULL COMMENT '用户名',
  `password` char(40) NOT NULL COMMENT '密码',
  `face` char(200) DEFAULT NULL COMMENT '头像',
  `email` char(50) NOT NULL COMMENT '电子邮件',
  `create` int(10) NOT NULL COMMENT '注册时间',
  `last_login` int(10) NOT NULL COMMENT '最后登录时间',
  `last_ip` int(10) NOT NULL COMMENT '最后登录ip',
  `sex` char(1) DEFAULT NULL COMMENT '性别',
  `nickname` char(20) DEFAULT NULL COMMENT '绰号',
  `team` char(20) DEFAULT NULL COMMENT '海贼团',
  `location` char(10) DEFAULT NULL COMMENT '故乡',
  `organ` char(30) DEFAULT NULL COMMENT '口头禅',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8;

